mapper.select:
select distinct m.menu_id, m.parent_id, m.menu_name, m.url, m.perms, m.target, m.menu_type, m.icon, m.order_num, m.create_time from sys_menu m left join sys_role_menu rm on m.menu_id = rm.menu_id left join sys_user_role ur on rm.role_id = ur.role_id LEFT JOIN sys_role ro on ur.role_id = ro.role_id where ur.user_id = #{params.userId}
AND m.menu_name like concat('%', #{menuName}, '%')
AND m.visible = #{visible}
order by m.parent_id, m.order_num
